import "jsr:@supabase/functions-js/edge-runtime.d.ts";

const OPENAI_API_KEY = Deno.env.get("OPENAI_API_KEY");
const OPENAI_BASE_URL = "https://api.openai.com/v1";

type RecipeSuggestionRequest = {
  pantryItems: string[];
  preferences?: {
    dietary?: string;
    cuisine?: string;
    difficulty?: string;
  };
};

type RecipeSuggestion = {
  name: string;
  ingredients: string[];
  instructions: string;
  prepTime: number;
  servings: number;
  category: string;
  matchPercentage: number;
};

Deno.serve(async (req) => {
  try {
    if (req.method !== "POST") {
      return new Response(JSON.stringify({ error: "Method Not Allowed" }), { status: 405 });
    }

    if (!OPENAI_API_KEY) {
      return new Response(
        JSON.stringify({ error: "OpenAI API key not configured" }),
        { status: 500 }
      );
    }

    const body = (await req.json()) as RecipeSuggestionRequest;
    const { pantryItems, preferences } = body;

    if (!pantryItems || pantryItems.length === 0) {
      return new Response(
        JSON.stringify({ error: "No pantry items provided" }),
        { status: 400 }
      );
    }

    // Build the prompt for OpenAI
    let prompt = `Based on the following ingredients available in the pantry: ${pantryItems.join(", ")}.\n\n`;
    prompt += "Please suggest 3-5 creative and practical recipes that can be made using these ingredients. ";
    prompt += "You can suggest recipes that use some or all of these ingredients. ";

    if (preferences?.dietary) {
      prompt += `Dietary preference: ${preferences.dietary}. `;
    }
    if (preferences?.cuisine) {
      prompt += `Cuisine preference: ${preferences.cuisine}. `;
    }
    if (preferences?.difficulty) {
      prompt += `Difficulty level: ${preferences.difficulty}. `;
    }

    prompt += "\n\nFor each recipe, provide:\n";
    prompt += "1. Recipe name\n";
    prompt += "2. List of ingredients needed (from the pantry items)\n";
    prompt += "3. Step-by-step cooking instructions\n";
    prompt += "4. Estimated preparation time in minutes\n";
    prompt += "5. Number of servings\n";
    prompt += "6. Category (e.g., Breakfast, Lunch, Dinner, Snack, Dessert)\n";
    prompt += "7. Match percentage (what percentage of ingredients are from the pantry)\n";
    prompt += "\nFormat the response as a JSON array of recipe objects.";

    const started = performance.now();

    // Call OpenAI API
    const chatRes = await fetch(`${OPENAI_BASE_URL}/chat/completions`, {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${OPENAI_API_KEY}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        model: "gpt-4o-mini",
        messages: [
          {
            role: "system",
            content: "You are a helpful cooking assistant that suggests creative recipes based on available ingredients. Always respond with valid JSON.",
          },
          {
            role: "user",
            content: prompt,
          },
        ],
        temperature: 0.8,
        max_tokens: 2000,
        response_format: { type: "json_object" },
      }),
    });

    if (!chatRes.ok) {
      const errorText = await chatRes.text();
      console.error("OpenAI API error:", errorText);
      return new Response(
        JSON.stringify({ error: "OpenAI API error", detail: errorText }),
        { status: 502 }
      );
    }

    const json: any = await chatRes.json();
    const content = json?.choices?.[0]?.message?.content;

    if (!content) {
      return new Response(
        JSON.stringify({ error: "Empty response from OpenAI" }),
        { status: 502 }
      );
    }

    // Parse the JSON response
    let recipes: RecipeSuggestion[];
    try {
      const parsed = JSON.parse(content);
      // Handle different response formats
      recipes = parsed.recipes || parsed.suggestions || parsed;

      // Ensure recipes is an array
      if (!Array.isArray(recipes)) {
        recipes = [recipes];
      }
    } catch (parseError) {
      console.error("Failed to parse OpenAI response:", parseError);
      return new Response(
        JSON.stringify({ error: "Failed to parse recipe suggestions" }),
        { status: 500 }
      );
    }

    const duration_ms = Math.round(performance.now() - started);

    return new Response(
      JSON.stringify({
        recipes,
        duration_ms,
        tokens: json?.usage,
      }),
      {
        status: 200,
        headers: { "Content-Type": "application/json" },
      }
    );
  } catch (e) {
    console.error("Unhandled error:", e);
    return new Response(
      JSON.stringify({ error: "Unhandled error", detail: String(e) }),
      { status: 500 }
    );
  }
});

