-- Create FoodsCache table for frequently searched items
create table if not exists public.foods_cache (
  id uuid primary key default gen_random_uuid(),
  food_name text not null,
  brand_name text,
  calories numeric,
  photo text,
  search_count integer default 1,
  last_searched_at timestamp with time zone default now(),
  created_at timestamp with time zone default now()
);

-- Enable RLS (public read access for cache)
alter table public.foods_cache enable row level security;

-- Create RLS policies - anyone can read cache
create policy "Anyone can view foods cache"
  on public.foods_cache for select
  using (true);

-- Only authenticated users can insert/update cache
create policy "Authenticated users can insert to foods cache"
  on public.foods_cache for insert
  with check (auth.role() = 'authenticated');

create policy "Authenticated users can update foods cache"
  on public.foods_cache for update
  using (auth.role() = 'authenticated');

-- Create indexes for faster queries
create index if not exists foods_cache_food_name_idx on public.foods_cache(food_name);
create index if not exists foods_cache_search_count_idx on public.foods_cache(search_count desc);
create index if not exists foods_cache_last_searched_idx on public.foods_cache(last_searched_at desc);

-- Create shopping_items table
create table shopping_items (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references auth.users(id) on delete cascade,
  name text not null,
  quantity numeric default 1,
  unit text default 'pcs',
  category text,
  checked boolean default false,
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Enable RLS
alter table shopping_items enable row level security;

-- Create RLS policies
create policy "Users can view their own shopping items" 
  on shopping_items for select 
  using (auth.uid() = user_id);

create policy "Users can insert their own shopping items" 
  on shopping_items for insert 
  with check (auth.uid() = user_id);

create policy "Users can update their own shopping items" 
  on shopping_items for update 
  using (auth.uid() = user_id);

create policy "Users can delete their own shopping items" 
  on shopping_items for delete 
  using (auth.uid() = user_id);

-- Create index for better performance
create index shopping_items_user_id_idx on shopping_items(user_id);
create index shopping_items_checked_idx on shopping_items(checked);

-- Add name column (alias for food_name for backward compatibility)
alter table pantry_items add column if not exists name text;

-- Update existing rows to copy food_name to name
update pantry_items set name = food_name where name is null;

-- Add indexes for better performance
create index if not exists pantry_items_user_id_idx on pantry_items(user_id);
create index if not exists pantry_items_expiration_date_idx on pantry_items(expiration_date);
create index if not exists pantry_items_category_idx on pantry_items(category);
create index if not exists pantry_items_created_at_idx on pantry_items(created_at desc);

-- Add trigger to keep name and food_name in sync
create or replace function sync_pantry_item_names()
returns trigger as $$
begin
  -- If food_name is set but name is not, copy food_name to name
  if new.food_name is not null and new.name is null then
    new.name := new.food_name;
  end if;

  -- If name is set but food_name is not, copy name to food_name
  if new.name is not null and new.food_name is null then
    new.food_name := new.name;
  end if;

  -- Update updated_at timestamp
  new.updated_at := now();

  return new;
end;
$$ language plpgsql;

create trigger sync_pantry_item_names_trigger
  before insert or update on pantry_items
  for each row
  execute function sync_pantry_item_names();

